export function assign(...args: any[]): any;

export function isApp(name?: string, ua?: string): boolean;
export function isAndroid(ua?: string): boolean;
export function isIOS(ua?: string): boolean;
export function isTablet(ua?: string): boolean;
export function isMobile(ua?: string): boolean;
export function isIPhone(ua?: string): boolean;
export function isIPad(ua?: string): boolean;
export function isWebview(name: string, ua?: string): boolean;
export function getNetwork(ua?: string): string;
export function getUUID(ua?: string): string;
export function getAppVersion(appName: string, ua?: string): string;
export function getAndroidVersion(ua?: string): string
export function getIOSVersion(ua?: string): string

export function getUrlQuery(url?: string, name?: string[] | string): string | Object;
