export interface Version {
	full: string;
	major: number;
	minor: number;
	patch: number;
	build: number;
}

export enum CompareResult {
	LT = -1,
	EQL = 0,
	GT = 1
}

export function version(v: string): Version;
export function versionCompare(a: string, b: string): CompareResult;
